import React from 'react'
import { useState, useEffect } from 'react'
export default function BlogPosts() {

  const [posts, setPosts] = useState([])

  const blogposts = [
    {
      title: "Post 1",
      body: "heres some sweet text"
    },
    {
      title: "Post 2",
      body: "heres some sour text"
    },
    {
      title: "Post 3",
      body: "heres some savory text"
    },
    {
      title: "Post 4",
      body: "heres some salty text"
    },
  ]

  useEffect(() => {
    setPosts(blogposts)
  }, [])

  const displayPosts = () => {
    return posts.map((post) => {
      return <Post title={post.title} body={post.body}></Post>
    })
  }

  return (
    <div>
      {posts ? displayPosts : "loading"}
    </div>
  )
}
