import React from 'react'
import { useState, useEffect } from 'react'
import Post from '../components/Post'
import Sidebar from './BlogTitleSide'
import { Route } from 'react-router-dom'
export default function BlogPosts() {

  const [posts, setPosts] = useState([])

  const blogposts = [
    {
      title: "Post 1",
      body: "heres some sweet text",
      id: 1
    },
    {
      title: "Post 2",
      body: "heres some sour text",
      id: 2
    },
    {
      title: "Post 3",
      body: "heres some savory text",
      id: 3
    },
    {
      title: "Post 4",
      body: "heres some salty text",
      id: 4
    },
  ]

  useEffect(() => {
    setPosts(blogposts)
  }, [])



  return (
    <div className="flex flex-row bg-gray-200">
      <Sidebar posts={posts} ></Sidebar>
      <Route path="/blog/:blogid" component={Post} ></Route>
    </div>
  )
}
