import React from 'react'
import { useState, useEffect } from 'react'
import Post from '../components/Post'
import Sidebar from './BlogTitleSide'
export default function BlogPosts() {

  const [posts, setPosts] = useState([])

  const blogposts = [
    {
      title: "Post 1",
      body: "heres some sweet text",
      id: 1
    },
    {
      title: "Post 2",
      body: "heres some sour text",
      id: 2
    },
    {
      title: "Post 3",
      body: "heres some savory text",
      id: 3
    },
    {
      title: "Post 4",
      body: "heres some salty text",
      id: 4
    },
  ]

  useEffect(() => {
    setPosts(blogposts)
  }, [])

  const displayPosts = () => {
    return posts.map(post => {
      return <Post key={post.id} title={post.title} body={post.body}></Post>
    })
  }

  const displayTitles = () => {
    return posts.map(post => {
      return <h1>{post.title}</h1>
    })
  }

  return (
    <div>
      <Sidebar posts={posts} className=""></Sidebar>
      {posts ? displayPosts() : "loading"}
    </div>
  )
}
