import React from 'react'
import { Link } from 'react-router-dom'
export default function BlogTitleSide({ posts }) {

  const displayTitles = () => {
    return posts.map(post => {
      return <Link className="my-10 hover:bg-blue-dark p-20" to={`/blog/${post.id}`}>{post.title}</Link>
    })
  }

  return (
    <nav className="bg-gray-300 w-auto px-12 text-center flex flex-col py-20">
      {displayTitles()}
    </nav>
  )
}
